import { ItemContext } from "../App";
import React, { useContext } from "react";

const TopNav = () => {
  const { tasks, count } = useContext(ItemContext);
  var search_terms = [tasks];

  function autocompleteMatch(input) {
    if (input == '') {
      return [];
    }
    var reg = new RegExp(input)
    return search_terms.filter(function(term) {
      if (term.match(reg)) {
        return term;
      }
    });
}

  return (
    <>
       <section id="heading">
            <div>
            <i className="uil uil-bars" >&emsp;</i><i className="uil uil-search"></i><input className="quickfind" type="text" placeholder="Quick Find" oninput={autocompleteMatch} style="font: Roboto"/>
            </div>
            <div className="right-heading"><i className="uil uil-check-circle"></i>{count}</div> 
        </section>
    </>
  );
};

export default TopNav;
