import React, { useContext } from "react";
import { ItemContext } from "../App";

const LeftNav = () => {
  const { category, setCategory, count } =
    useContext(ItemContext);
  const changeCategory = event => {
    if (event.innerText === "Inbox")  {
      setCategory(event.innerText);
    } 
    else if (event.innertext === "Today")  {
      setCategory(event.innerText);
    }
    else if (event.innerText === "Upcoming") {
      setCategory(event.innerText)
    }
    else {
      setCategory(category)
    }
    }

  var btn = document.getElementsByClassName("collapsible");

  btn[0].addEventListener("click", function () {
    this.classList.toggle("active");
    var content = this.nextElementSibling;
    if (content.style.display === "block") {
        content.style.display = "none";
    } else {
        content.style.display = "block";
    }
})
  return (
    <>
            <section id="left-navigation">
            <ul>
                <ul class="first">
                    <li class="uil uil-inbox"><button type="button" onClick={changeCategory}>&emsp;Inbox&emsp;<span class="task-count">{count}</span></button></li>
                    <li class="uil uil-calendar-alt"><button type="button" onClick= {changeCategory}>&emsp;Today&emsp;<span class="task-count">{count}</span></button></li>
                    <li class="uil uil-schedule"><button type="button" onClick= {changeCategory}>&emsp;Upcoming</button><span class="task-count">{count}</span></li>
                    <li class="uil uil-angle-down">&emsp;Projects</li>
                </ul>
                <li onclick="myFunction(this)" class="uil uil-angle-down" id="arrow"><button type="button" class="collapsible">&emsp;&emsp;<i class="uil uil-circle" id="circle"></i>&emsp;Work</button>
                <div class="content">
                    <ul class="second">
                        <li class="uil uil-circle">&emsp;New Brand&emsp;<span class="task-count">9</span></li>
                        <li class="uil uil-user">&emsp;Website Update&emsp;<span class="task-count">11</span></li>
                        <li class="uil uil-user">&emsp;Product Roadmap&emsp;<span class="task-count">11</span></li>
                        <li class="uil uil-user">&emsp;Meeting Agenda&emsp;<span class="task-count">6</span></li>
                    </ul>
                </div>
                </li>
                <li class="uil uil-angle-down" id="arrow"><button type="button" class="collapsible">&emsp;&emsp;<i class="uil uil-circle" id="circle"></i>&emsp;Personal</button>
                    <div class="content">
                    </div>
                    </li>
                <ul class="third">
                    <li class="uil uil-plus">&emsp;Add Project</li>
                </ul>
                </ul>
        </section>
            );
    </>
  );
};

export default LeftNav;
