import React, { useContext } from "react";
import { ItemContext } from "../App";

const MainBody = () => {
  const { tasks, setTasks, category, count, setCount} =
    useContext(ItemContext);
  const complete = event => {
    if (event.target.style.textDecoration) {
      event.target.style.removeProperty('text-decoration');
    } else {
      event.target.style.setProperty('text-decoration', 'line-through');
    }
  };
  const handleClick2 = (e) => {
    let newTask = tasks
    tasks.push(
      {"category": category,
        "name": "",
      "isComplete": false
  });
  setTasks(newTask)
  console.log(tasks)
  setCount(tasks.length)
}
const item = tasks.map(task => 
  <li><input type="checkbox" onClick={complete} id="check" name="check" value="check" />&emsp;{task.name}</li>)  

return (
    <>

          <section id="main-nav">
          <h1>Inbox</h1>
          <ul class="fourth" onclick="changeIcon()" id="myBtn">
              {item}
              <li class="uil uil-plus"><button onClick={handleClick2} type="button">&emsp;Add Task</button></li>
          </ul>
      </section>
    </>
  );
};

export default MainBody;
