import React, { useContext } from "react";
import { ItemContext } from "../App";

const MainBody = () => {
  var { tasks, setTasks, category, setCount} =
    useContext(ItemContext);
  const complete = event => {
    if (event.target.style.textDecoration) {
      event.target.style.removeProperty('text-decoration');
    } else {
      event.target.style.setProperty('text-decoration', 'line-through');
    }
  };

  let todos = [];

  for (const key of Object.keys(tasks)) {
      if (key.includes(category)) {             
          todos.push(tasks[key]) 
      }
  }

  const addTask = (e) => {
    console.log(e)
    let newTask = tasks
    tasks.push(
      {"category": category,
        "name": "",
      "isComplete": false
  });
  setTasks(newTask)
  console.log(tasks)
  setCount(todos.length)
}
const item = todos.map((task, index) => 
  <li><input key={index} type="checkbox" onClick={complete} id="check" name="check" value="check" />&emsp;{task.name}</li>)  

return (
    <>

          <section id="main-nav">
          <h1>{category}</h1>
          <ul className="fourth" id="myBtn">
              {item}
              <li className="uil uil-plus">&emsp;<form onSubmit={e => addTask(e)}><input id = "addTask" type="text" placeholder="Add Task" ></input><button type = "submit" style={{display: "hidden"}}></button></form></li>
          </ul>
      </section>
    </>
  );
};

export default MainBody;
