import React, { useContext } from "react";
import { ItemContext } from "../App";

/*add tasks and save to json file. once saved, check category of json element and print json name item as list item.
count each item of a category and each item overall.
strike through task and change iscomplete value to complete.
be able to edit tasks*/


const MainBody = () => {
  var { tasks, setTasks, category, setCount, totalList, setTotalList} =
    useContext(ItemContext);

    const complete = event => {
    if (event.target.style.textDecoration) {
      event.target.style.removeProperty('text-decoration');
    } else {
      event.target.style.setProperty('text-decoration', 'line-through');
    }
  };


  function addTask(e) {
    console.log(e)
    let newTask = totalList
    newTask.push(
      {"category": category,
        "name": e,
        "isComplete": false
  });
  setTotalList(newTask)
  console.log(totalList)
  setCount(totalList.length)
}
const item = totalList.map((task, index) => 
  <li><input key={index} type="checkbox" onClick={complete} id="check" name="check" value="check" />&emsp;{task.name}</li>)  

return (
    <>

          <section id="main-nav">
          <h1>{category}</h1>
          <ul className="fourth" id="myBtn">
              {item}
              <li className="uil uil-plus">&emsp;<form onClick={addTask(this)}><input id = "addTask" type="text" placeholder="Add Task" ></input><button type = "submit" style={{display: "hidden"}}></button></form></li>
          </ul>
      </section>
    </>
  );
};

export default MainBody;
