import React, { useContext } from "react";
import { ItemContext } from "../App";

const MainBody = () => {
  const { tasks, setTasks, category, setCount} =
    useContext(ItemContext);
  const complete = event => {
    if (event.target.style.textDecoration) {
      event.target.style.removeProperty('text-decoration');
    } else {
      event.target.style.setProperty('text-decoration', 'line-through');
    }
  };
  const addTask = (e) => {
    let newTask = tasks
    tasks.push(
      {"category": category,
        "name": "",
      "isComplete": false
  });
  setTasks(newTask)
  console.log(tasks)
  setCount(tasks.length)
}
const item = tasks.map(task => 
  <li><input type="checkbox" onClick={complete} id="check" name="check" value="check" />&emsp;{task.name}</li>)  

return (
    <>

          <section id="main-nav">
          <h1>{category}</h1>
          <ul className="fourth" id="myBtn">
              {item}
              <li className="uil uil-plus">&emsp;<input type="submit" /><input onSubmit={addTask} type="text" placeholder="Add Task"></input></li>
          </ul>
      </section>
    </>
  );
};

export default MainBody;
