  const { spaces, setSpaces, count, setCount } =
    useContext(ItemContext);
  return (
    <>
        {spaces.length == 0 ? (
          <h4>No items in cart</h4>
        ) : (
          spaces.map((space) => {
            return (
              <section id="left-navigation">
              <ul>
                  <ul class="first">
                      <li class="uil uil-inbox">&emsp;Inbox&emsp;<span class="task-count">5</span></li>
